css:
@import url('https://fonts.googleapis.com/css2?family=Almarai:wght@300;400;700;800&family=Cairo:wght@200..1000&family=Underdog&display=swap');

.print-format {
    font-size: 9pt;
    font-family: "Almarai", sans-serif;
    font-style: normal;
}

.letter-head-footer { 
    margin-left:25mm;
    margin-right:20mm;
    position: absolute; 
    bottom: 9mm
}

.page-number {
    position: absolute; 
    bottom: 5mm; 
    left: 100mm; 
    overflow: visible
}

.sap { 
    font-weight: bold; 
    font-size: 11pt; 
}

.signatures {
    display: flex;
    justify-content: center;
    gap: 350px;
    margin: 1px 3px;
}


.invoice {
    display: flex;
    justify-content: space-between;
    padding: 1px;
    width: 500px;
}
      
.important {
    font-weight: bold;
}
        
.divider{
    border-left: 2px solid black;
    height: auto;
    margin: 4px 15px;
    margin-right: 0px;
    margin-left: 3px;
}

.right-section, .left-section {
    width: 34%;
}

.table th {
    font-weight:bold;
}
        
        
.table td, .table th {
    border: 1px solid #000 !important;
    font-weight: normal;
    color: #000;
    text-align:center;
    font-size: 11px;
    padding: 4px 3px !important;
    word-wrap: break-word;
    /*border-collapse:collapse;*/
    /*border-radius:5% !important;*/
}

/*@media print {*/
/*    .table td, .table th {*/
/*        word-wrap: normal;*/
/*    }*/
/*}*/

.table {
    margin: 20px 0px !important;
    width:100%;
    border-collapse: separate;
    /*border: 1px solid black !important;*/
    /*border-collapse:separate;*/
    /*overflow:hidden;*/
    /*border-radius:3% !important;*/
}
.print-format img {
    max-width: 500;
}

.invoice { 
    display: flex; 
    justify-content: space-between; 
    padding: 2px; 
    width: 100%; 
}
    
.divider { 
    border-left: 2px solid black; 
    height: auto; 
    margin: 4px 15px; 
}

.total-rows td {
    background-color:#fffae1 !important;
}
   
.table th {
    background-color:#fffae1;
}

.table td {
    background-color:#fffae1;
}

.qr { 
    display: flex; 
    align-items: center; 
    margin-left: 20px; 
    max-width: 500px;
    flex-direction: column; 
}

@media print{
    .print-format {
        padding:2px !important;
    }
    
    .table {
        border-collapse:separate !important;
        border: 1px solid black !important;
    }
    
    .table td, .table th {
        border: 1px solid #000 !important;
    }
}

html:
{%- from "templates/print_formats/standard_macros.html" import add_header, render_field, print_value, fieldmeta, get_width, get_align_class with context -%}

{% if print_settings.repeat_header_footer %}
    <div id="header-html">
        {% if letter_head and not no_letterhead %}
            <div class="letter-head">{{ letter_head }}</div>
        {% else%}
             <div>
               <table>
                <tr></tr>
               </table>
               </div>
        {% endif %}
   </div>
{% endif %}

<div class="invoice-section">
    <!--Heading-->
    <div style="text-align: center;">
        <p style="text-algin: center; font-size:18px; font-weight: bold; margin-bottom:15px;">{{ _("Partial Invoice Template") }}</p>
    </div>

    <!-- Details Section -->
    <div class="invoice">

        <!--Right Section-->
        <div class="right-section">
            {% set custom_government_contract_no = frappe.db.get_value("Project", doc.project, "custom_government_contract_no")%}

            <p><span class="important"  >{{ _("Invoice To") }}:</span>  {{doc.customer_name}}</p>
            <p><span class="important">{{ _("Address")}}:</span> {{ doc.address_display or '' }}</p>
            <p><span class="important">{{_("Tax No") }}:</span> {{ doc.tax_id or '' }}</p>
            <p><span class="important">{{_("Contract No")}}:</span> {{custom_government_contract_no or ''}}</p>
            <p><span class="important">{{_("Customer Ref")}}:</span> {{doc.custom_customer_reference}}</p>
        </div>

        <!--Divider-->
        <div class="divider"style="border:none;padding: 0 10px;"></div>

        <!-- QR Code -->
        <div class=" qr">
            {% set files = frappe.get_all('File', filters={'attached_to_doctype': 'Sales Invoice', 'attached_to_name': doc.name, 'is_private':0}, fields=['file_name', 'file_url'], limit=1) %}
            {%- for file in files -%}
               <img src="{{ file.file_url}}" alt="{{ file.file_name }}" />
            {%- endfor -%} 
        </div>

        <!--Middle Section-->
        <div class="middle-section" style="width:20%;">
            <p><span class="important">{{_("Invoice Date")}}:<br></span> {{ doc.posting_date }}</p>
            <p><span class="important">{{_("Invoice No")}}:<br></span> {{ doc.name }}</p>
            <p style="color:red;text-align:right;font-size:15px"> <b>{{_("Partial Invoice")}} </b></p>
        </div>

        <div class="divider"></div>

        <!--Left Section-->
        <div class="left-section">
            <p class="text-center"><span class="important">مؤسسة السهلي للمقاولات</span></p>
            <p><span class="important">{{_("Address")}}:</span> جدة، حي المروة، 3 مقابل شينك المطار</p>
            <p><span class="important">{{_("Bo Box")}}:</span> ص.ب 7238 جدة 21462</p>
            <p><span class="important">{{_("Phone No")}}:</span> 012 658 2222</p>
            <p><span class="important">{{_("Mobile")}}:</span> 059 329 8963</p>
            <p><span class="important">{{_("Email")}}:</span> invoice@alsalhy.net</p>
            <p><span class="important">{{_("Company CR ")}}:</span> 4030009912</p>
            <p><span class="important">{{_("VAT Number")}}:</span> 300189259400003</p>
        </div>
    </div>

    <table class="table " style="table-layout: fixed; margin: 2px 0; text-align: center;">
        {% set data = get_final_part_invoice_template_data(doc.items, doc)%}

        {% set ds = namespace(discount=0) %} 
        {% set pl = namespace(penalty=0) %}
        {% set cs = namespace(colspan=0) %}
        {% for d in data %}
            {% if d.custom_item_discount_amount > 0 %}
                {% set ds.discount = 1 %}
            {% endif %}

            {% if d.penalty_amnt > 0 %}
                {% set pl.penalty = 1 %}
            {% endif %}
        {% endfor %}

        {% if ds.discount == 1 and pl.penalty == 1 %}
            {% set cs.colspan = 14 %}
        {% elif ds.discount == 1 and pl.penalty == 0 %}
            {% set cs.colspan = 13 %}
        {% elif ds.discount == 0 and pl.penalty == 1 %}
            {% set cs.colspan = 12 %}
        {% elif ds.discount == 0 and pl.penalty == 0 %}
            {% set cs.colspan = 11 %}
        {% endif %}

        <thead>
            <tr>
                <th style="width: 3%;  line-height: 1; border-radius: 0px 10px 0px 0px; border-bottom:none !important; border-left:none !important;">{{_("Sr")}}</th>
                <th style="width: 8%;  line-height: 1; border-bottom:none !important; border-left:none !important;">{{_("Work Order No")}}</th>
                <th style="width: 7%;  line-height: 1; border-bottom:none !important; border-left:none !important;">{{_("Work Order Type")}}</th>
                <th style="width: 21%; line-height: 1; border-bottom:none !important; border-left:none !important;" colspan="2">{{_("Work Order Description")}}</th>
                <th style="width: 7%;  line-height: 1; border-bottom:none !important; border-left:none !important;">{{_("Cost Holder")}}</th>
                <th style="width: 9%; line-height: 1; border-bottom:none !important; border-left:none !important;" colspan="2">{{_("Achievment Date")}}</th>
                {% if ds.discount == 1 %}
                    <th style="width: 7%;  line-height: 1; border-bottom:none !important; border-left:none !important;">{{_("Total Amount Before Discount")}}</th>
                    <th style="width: 6%;  line-height: 1; border-bottom:none !important; border-left:none !important; ">{{_("Item Discount Amount")}}</th>
                {% endif %}
                <th style="width: 7%;  line-height: 1; border-bottom:none !important; border-left:none !important;">{{_("Final Taxable Amount")}}</th>
                <th style="width: 4%;  line-height: 1; border-bottom:none !important; border-left:none !important;">{{_("Tax ")}} %</th>
                <th style="width: 7%;  line-height: 1; border-bottom:none !important; border-left:none !important;">{{_("Tax Amount")}}</th>
                {% if pl.penalty == 1 %}
                    <th style="width: 7%;  line-height: 1; border-bottom:none !important; border-left:none !important;">{{_("Penalty Amount")}}</th>
                {% endif %}

                <th style="width: 7%;  line-height: 1; border-radius: 10px 0px 0px 0px; border-bottom:none !important;">{{_("Total Included Tax")}}</th> 
            </tr>
        </thead>

        <tbody>

            <!--Items Row-->



            {% for d in data %}
                <tr>
                    <td style="width: 3%;  line-height: 1; background-color:#ffffff; border-bottom:none !important; border-left:none !important;">{{ loop.index }}</td>
                    <td style="width: 8%;  line-height: 1; background-color:#ffffff; background-color:#ffffff; border-bottom:none !important; border-left:none !important;">{{ d.work_order_no or '' }}</td>
                    <td style="width: 7%;  line-height: 1; background-color:#ffffff; border-bottom:none !important; border-left:none !important;">{{ d.work_order_type or '' }}</td>
                    <td style="width: 21%;  line-height: 1; background-color:#ffffff; border-bottom:none !important; border-left:none !important;" colspan="2">{{ d.work_order_description or '' }}</td>
                    <td style="width: 7%;  line-height: 1; background-color:#ffffff; border-bottom:none !important; border-left:none !important;">{{ d.cost_holder or  ""}}</td>
                    <td style="width: 9%;  line-height: 1; background-color:#ffffff; border-bottom:none !important; border-left:none !important;" colspan="2">{{ frappe.utils.get_datetime(d.achievment_date).strftime("%d-%m-%Y") }}</td>
                    {% if ds.discount == 1 %}
                        {% if d.total_amnt_before_discount < 0 %}
                            <td style="width: 7%;  line-height: 1; background-color:#ffffff; color:red; border-bottom:none !important; border-left:none !important;">{{ frappe.format("%.2f"|format(d.total_amnt_before_discount), "Currency")|replace("ر.س", "") }}</td>
                        {% else %}
                            <td style="width: 7%;  line-height: 1; background-color:#ffffff; border-bottom:none !important; border-left:none !important;">{{ frappe.format("%.2f"|format(d.total_amnt_before_discount), "Currency")|replace("ر.س", "") }}</td>
                        {% endif %}
                        <td style="width: 6%;  line-height: 1; background-color:#ffffff; color:red; border-bottom:none !important; border-left:none !important;">{{ frappe.format("%.2f"|format(d.custom_item_discount_amount), "Currency")|replace("ر.س", "") }}</td>

                    {% endif %}
                    <td style="width: 7%;  line-height: 1; background-color:#ffffff; border-bottom:none !important; border-left:none !important;">{{ frappe.format("%.2f"|format(d.total_amnt_before_discount - d.custom_item_discount_amount), "Currency")|replace("ر.س", "") }}</td>
                    <td style="width: 4%;  line-height: 1; background-color:#ffffff; border-bottom:none !important; border-left:none !important;">{{ d.tax }}</td>
                    <td style="width: 7%;  line-height: 1; background-color:#ffffff; border-bottom:none !important; border-left:none !important;">{{ frappe.format("%.2f"|format(d.tax_amnt), "Currency")|replace("ر.س", "") }}</td>
                    {% if pl.penalty == 1 %}
                        <td style="width: 7%;  line-height: 1; background-color:#ffffff; color:red; border-bottom:none !important; border-left:none !important;">{{ frappe.format("%.2f"|format(d.penalty_amnt), "Currency")|replace("ر.س", "") }}</td>
                    {% endif %}
                    <td style="width: 7%;  line-height: 1; background-color:#ffffff; border-bottom:none !important;">{{ frappe.format("%.2f"|format(d.total_included_tax), "Currency")|replace("ر.س", "") }}</td> 
                </tr>

            {% endfor %}

           {% if ds.discount == 1 %} 

            <tr class="total-rows">
                <td colspan={{ cs.colspan }} style="text-align:right; border-bottom:none !important; border-left:none !important;">{{_("Total Amount Before Discount ")}} </td>
                {% if (data|sum(attribute='total_amnt_before_discount')) < 0 %}
                    <td colspan="1" style="color:red; text-align:right; border-bottom:none !important;">{{ frappe.format("%.2f"|format(data|sum(attribute='total_amnt_before_discount')), "Currency")|replace("ر.س", "") }}</td>
                {% else %}
                   <td colspan="1" style="text-align:right; border-bottom:none !important;">{{ frappe.format("%.2f"|format(data|sum(attribute='total_amnt_before_discount')), "Currency")|replace("ر.س", "") }}</td>
                {% endif %}

            </tr>

            <tr class="total-rows">
                <td colspan={{ cs.colspan }} style="text-align:right; color:red; border-bottom:none !important; border-left:none !important;"> {{_("Total Discount")}} </td>
                {% if (data|sum(attribute='custom_item_discount_amount')) < 0 %}
                    <td colspan="1" style="color:red; text-align:right; color:red; border-bottom:none !important;">{{  frappe.format("%.2f"|format(data|sum(attribute='custom_item_discount_amount')), "Currency")|replace("ر.س", "") }}</td>
                {% else %}
                    <td colspan="1" style="text-align:right; color:red; border-bottom:none !important;">{{  frappe.format("%.2f"|format(data|sum(attribute='custom_item_discount_amount')), "Currency")|replace("ر.س", "") }}</td>
                {% endif %}

            </tr>

            {% endif %}

            <tr class="total-rows">
                <td colspan={{ cs.colspan }} style="text-align:right; border-bottom:none !important; border-left:none !important;"> {{_("Total Taxable Amount")}}  </td>
                {% if (data|sum(attribute='total_amnt_before_discount') - (data|sum(attribute='custom_item_discount_amount'))) < 0 %}
                    <td colspan="1" style="color:red; text-align:right; border-bottom:none !important;">{{ frappe.format("%.2f"|format(data|sum(attribute='total_amnt_before_discount') - (data|sum(attribute='custom_item_discount_amount'))), "Currency")|replace("ر.س", "") or "" }}</td>
                {% else %}
                    <td colspan="1" style="text-align:right; border-bottom:none !important;">{{ frappe.format("%.2f"|format(data|sum(attribute='total_amnt_before_discount') - (data|sum(attribute='custom_item_discount_amount'))), "Currency")|replace("ر.س", "") or "" }}</td>
                {% endif %}

            </tr>

            <tr class="total-rows">
                <td colspan={{ cs.colspan }} style="text-align:right; border-bottom:none !important; border-left:none !important;"> {{_("Total VAT Amount")}}</td>
                {% if (data|sum(attribute='tax_amnt')) < 0 %}
                     <td colspan="1" style="color:red; text-align:right; border-bottom:none !important;">{{  frappe.format("%.2f"|format(data|sum(attribute='tax_amnt')), "Currency")|replace("ر.س", "") }} </td>
                {% else %}
                     <td colspan="1" style="text-align:right; border-bottom:none !important;">{{  frappe.format("%.2f"|format(data|sum(attribute='tax_amnt')), "Currency")|replace("ر.س", "") }} </td>
                {% endif %}

            </tr>

            <tr class="total-rows">
                <td colspan={{ cs.colspan }} style="text-align:right; border-bottom:none !important; border-left:none !important;">  {{_("Total Amount Including VAT")}}</td>
                {% if (data|sum(attribute='total_included_tax')) < 0 %}
                    <td colspan="1" style="color:red; text-align:right; border-bottom:none !important;">{{  frappe.format("%.2f"|format(data|sum(attribute='total_included_tax')), "Currency")|replace("ر.س", "") }} </td>
                {% else %}
                    <td colspan="1" style="text-align:right; border-bottom:none !important;">{{  frappe.format("%.2f"|format(data|sum(attribute='total_included_tax')), "Currency")|replace("ر.س", "") }} </td>
                {% endif %}

            </tr>
            {% if pl.penalty == 1 %}
                <tr class="total-rows">
                    <td colspan={{ cs.colspan }} style="text-align:right; color:red; border-bottom:none !important; border-left:none !important;"> {{_("Total fines not subject to VAT")}} </td>
                    {% if (doc.discount_amount) < 0 %}
                        <td colspan="1" style="color:red; text-align:right; color:red; border-bottom:none !important;">{{ frappe.format(doc.discount_amount, "Currency")|replace("ر.س", "") or "" }} </td>
                    {% else %}
                        <td colspan="1" style="text-align:right; color:red; border-bottom:none !important;">{{ frappe.format(doc.discount_amount, "Currency")|replace("ر.س", "") or "" }} </td>
                    {% endif %}

                </tr>

                <tr class="total-rows">
                    <td colspan={{ cs.colspan }} style="text-align:right;  border-radius: 0px 0px 10px 0px; border-bottom:none !important; border-left:none !important;"> {{_("Net Total Including Amount")}} </td>
                    {% if ((data|sum(attribute='total_included_tax'))) < 0 %}
                        <td colspan="1" style="color:red; text-align:right;  border-radius: 0px 0px 0px 10px; border-bottom:none !important;">{{ frappe.format(("%.2f"|format(data|sum(attribute='total_included_tax')))|float - doc.discount_amount, "Currency")|replace("ر.س", "") or "" }} </td>
                    {% else %}
                        <td colspan="1" style="text-align:right;  border-radius: 0px 0px 0px 10px; border-bottom:none !important;">{{ frappe.format(("%.2f"|format(data|sum(attribute='total_included_tax')))|float - doc.discount_amount, "Currency")|replace("ر.س", "") or "" }} </td>
                    {% endif %}
                </tr>
            {% endif %}


            <!-- Payment Entry Data-->
            {% set pe_data = get_payment_entry_data(doc.name, ((doc.rounded_total - doc.discount_amount) + ((data|sum(attribute='total_billed_amnt')) * 0.15))) %}

            <!--Returned SI Data-->
            {% set return_data = get_returned_si_amount(doc.name) %}

            <!--Journal Entry Data-->
            {% set je_data = get_journal_entry_data(doc.name) %}  

            <!--Ordered Data-->
            {% set ti = namespace(total=0) %}
            {% set ordered_data = get_ordered_deduction_data(pe_data, return_data, je_data) %}
            {% if ordered_data != None or ordered_data != [] %}
                {% for od in ordered_data %}
                    {% set ti.total = ti.total + od.amount %}
                    <tr>
                        <td colspan={{ cs.colspan }} style="text-align:right; border:1px solid black; color:red; border-bottom:none !important; border-left:none !important;">{{ _(od.rowname) }}  </td>
                        <td colspan="1" style="text-align:right; border:1px solid black; color:red; border-bottom:none !important;">{{ frappe.format(od.amount ,"Currency")|replace("ر.س", "") or "" }} </td>
                    </tr>
                {% endfor%}
            {% endif %}

            <!--Outstanding Data-->
                {% if ti.total > 0 %}
                    <tr class="total-rows">
                        <td colspan={{ cs.colspan }} style="text-align:right; border:1px solid black; color:red; border-radius: 0px 0px 10px 0px;  border-left:none !important;">{{ _("Outstanding Invoiced Amount") }}</td>
                        <td colspan="1" style="color:red; text-align:right; border:1px solid black; border-radius: 0px 0px 0px 10px; ">{{ frappe.format(("%.2f"|format(data|sum(attribute='total_included_tax') - ti.total)) , "Currency")|replace("ر.س", "") or "" }} </td>
                    </tr>
                {% endif %}
        </tbody>
    </table>

    <div class="signatures">
        <div class="signature"><span class="sap">{{_("Contractor Signature")}}</span></div>
        <div class="signature"><span class="sap">{{_("Contactor Stamp")}}</span></div>
    </div>
</div>

{% if print_settings.repeat_header_footer %}
<div id="footer-html">
    {% if footer %}
    <div class="letter-head-footer"> {{ footer }} </div>
    {% endif %}
    <p class="text-center small page-number visible-pdf">
        {{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }} </p>
</div>
{% endif %}
